import React, { Component } from 'react';
import './Profile.css';

class Profile extends Component {
  constructor(props) {
    super(props)
    this.state = {
      name: this.props.user.name,
      age: this.props.user.age,
      pet: this.props.user.pet
    }
  }

  onProfileUpdate = (data) => {
    fetch(`https://fast-reef-61278.herokuapp.com/profile/${this.props.user.id}`, {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
         body: JSON.stringify({formInput: data}) 
    }).then(resp => {
        this.props.toggleModal();
        this.props.loadUser({ ...this.props.user, ...data });
    }).catch(console.log)
  }

  onFormChange = (event) => {
    switch(event.target.name) {
      case 'user-name':
        this.setState({name: event.target.value})
        break;
      case 'user-age':
        this.setState({age: event.target.value})
        break;
      case 'user-pet':
        this.setState({pet: event.target.value})
        break;
      default:
        return;
    }
  }

  render() {
    const { toggleModal, user } = this.props
    const { name, age, pet } = this.state;
    return (
      <div className='profile-modal'>
        <article className='br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center bg-white'>
          <main className='pa4 black-80 w-80'>
            <img
              src='https://pbs.twimg.com/profile_images/707386802215247872/bO-U0PTv.jpg'
              className='h3 w3 dib' alt='avatar'
            />
            <h1>{name}</h1>
            <h2>{age}</h2>
            <h3>{pet}</h3>
            <h4>{`Images submitted: ${user.entries}`}</h4>
            <p>{`Member since: ${new Date(user.joined).toLocaleDateString()}`}</p>
            <hr />
            <label className='mt2 fw6' htmlFor='user-name'>Name:</label>
            <input onChange={this.onFormChange} type='text' name='user-name' className='pa2 ba w-100' placeholder={name}></input>
            {/*<label className='mt2 fw6' htmlFor='user-age'>Age:</label>
            <input onChange={this.onFormChange} type='text' name='user-age' className='pa2 ba w-100' placeholder={age}></input>
            <label className='mt2 fw6' htmlFor='user-pet'>Pet:</label>
            <input onChange={this.onFormChange} type='text' name='user-pet' className='pa2 ba w-100' placeholder={pet}></input>*/}
            <div className='mt4' style={{ display: 'flex', justifyContent: 'space-evenly'}}>
              <button className='b pa2 grow pointer hover-white w-40 bg-light-blue b--black-20'
                onClick={() => this.onProfileUpdate({ name, age, pet })}>
                Save
              </button>
              <button className='b pa2 grow pointer hover-white w-40 bg-light-red b--black-20'
                onClick={toggleModal}>
                Cancel
              </button>
            </div>

          </main>
          <div className='modal-close' onClick={toggleModal}>
            &times;
          </div>
        </article>
      </div>
    );
  }
}

export default Profile;
